mapper.select:
select

from form
where openid = #{openid, jdbcType=VARCHAR}

select avatar
from form
where id = #{id, jdbcType=INTEGER}

select

from form

    
        and id = #{id, jdbcType=INTEGER}
    
    
        and first_dep = #{dep, jdbcType=VARCHAR}
    
    
        and gender = #{gender, jdbcType=VARCHAR}
    
    
        and name = #{name, jdbcType=VARCHAR}
